package flowcontrolstatements;

public class Switchclass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String fooditem = "pizza";

		switch (fooditem) {
		case "Pasta":
			System.out.println("Here is your Pasta");
			break;

		case "pizza":
			System.out.println("Here is your pizza");
			break;

		case "noodles":
			System.out.println("Here is your noodles");
			break;

		case "burger":
			System.out.println("Here is your burger");
			break;

		case "mocha":
			System.out.println("Here is your mocha");
			break;
		}
	}
}
